        deleteDir.setAttribute('disabled', 'true');
    }
    if (listbox.selectedCount == 1 && listbox.selectedIndex != listbox.getRowCount()-1) {
        moveDownDir.removeAttribute('disabled');
    } else {
        moveDownDir.setAttribute('disabled', 'true');
