    log.exception(e);
}


try {
    var si = this.treeView.selection.currentIndex;
    var curItem = this.treeChildren.childNodes[si];
    if (curItem.nextSibling && curItem.nextSibling.nextSibling) {
        curItem.parentNode.insertBefore(curItem,
                                    curItem.nextSibling.nextSibling);
        this.treeView.selection.select(si+1);
    } else {
        curItem.parentNode.appendChild(curItem);
        this.treeView.selection.select(this.treeView.rowCount-1);
    }
    this.updateButtons();
} catch (e) {
    log.exception(e);
}


// Insert a <box overflow=hidden><label/><box> somewhere
// visible so we can measure the text.  Note that we can't
// just stick it inside anonymous content here, since we might
// not be visible yet.
var doc = this.ownerDocument;
var box = doc.createElementNS(this.namespaceURI, "box");
box.setAttribute("style", "overflow: hidden; width: 0; height: 0;");
var measure = doc.createElementNS(this.namespaceURI, "label");
box.appendChild(measure);
doc.documentElement.appendChild(box);
measure.value = this._lastText;
var {width, height} = measure.getBoundingClientRect();
doc.documentElement.removeChild(box);

// Now that we have the dimensions, use it to set things
this._label.style.width = height + "px";
this._label.style.height = width + "px";
this.width = height;
this._label.value = this._lastText;
this.setAttribute("value", this._lastText);

property.setter:
if (val == this._lastText) return;
this._lastText = val;
this.refresh();
return val;

handlers.handler:

switch (event.attrName) {
    case "value": {
        this.value = event.newValue;
        break;
    }
    case "collapsed": {
        // need to re-measure the text
        this.refresh();
        break;
    }
}
